(function() {
  // requestAnimationFrameã®ãƒãƒªãƒ•ã‚£ãƒ«
  const requestAnimFrame = window.requestAnimationFrame || 
                          window.webkitRequestAnimationFrame || 
                          window.mozRequestAnimationFrame || 
                          function(callback) { window.setTimeout(callback, 1000/60); };

  // ã‚­ãƒ£ãƒ³ãƒã‚¹ä½œæˆé–¢æ•°
  function createCanvas(width, height) {
    const canvas = document.createElement('canvas');
    canvas.width = width;
    canvas.height = height;
    canvas.style.position = 'absolute';
    canvas.style.top = '0';
    canvas.style.left = '0';
    canvas.style.width = '100%';
    canvas.style.height = '100%';
    canvas.style.pointerEvents = 'none';
    canvas.style.zIndex = '-1';
    canvas.style.opacity = '0.3';
    canvas.style.mixBlendMode = 'screen';
    document.querySelector('body').appendChild(canvas);
    return canvas;
  }

  // æµä½“ã‚·ãƒŸãƒ¥ãƒ¬ãƒ¼ã‚·ãƒ§ãƒ³
  class FluidSimulation {
    constructor(width, height) {
      this.canvas = createCanvas(width, height);
      this.ctx = this.canvas.getContext('2d');
      this.width = width;
      this.height = height;
      this.particles = [];
      this.flowField = [];
      this.init();
    }

    init() {
      // æµä½“å ´ã®åˆæœŸåŒ–
      for (let y = 0; y < this.height; y += 20) {
        for (let x = 0; x < this.width; x += 20) {
          this.flowField.push({
            x: x,
            y: y,
            angle: Math.random() * Math.PI * 2,
            strength: Math.random() * 0.5 + 0.1
          });
        }
      }

      // ãƒ‘ãƒ¼ãƒ†ã‚£ã‚¯ãƒ«ã®åˆæœŸåŒ–
      for (let i = 0; i < 100; i++) {
        this.particles.push({
          x: Math.random() * this.width,
          y: Math.random() * this.height,
          size: Math.random() * 3 + 1,
          speedX: 0,
          speedY: 0,
          color: `hsla(${Math.random() * 60 + 200}, 100%, 70%, 0.3)`
        });
      }

      this.animate();
    }

    // æœ€ã‚‚è¿‘ã„æµä½“å ´ã®ã‚¤ãƒ³ãƒ‡ãƒƒã‚¯ã‚¹ã‚’å–å¾—
    getClosestFlowField(x, y) {
      let closestIndex = 0;
      let closestDist = Number.MAX_VALUE;

      for (let i = 0; i < this.flowField.length; i++) {
        const field = this.flowField[i];
        const dx = field.x - x;
        const dy = field.y - y;
        const dist = dx * dx + dy * dy;

        if (dist < closestDist) {
          closestDist = dist;
          closestIndex = i;
        }
      }

      return this.flowField[closestIndex];
    }

    // ãƒ‘ãƒ¼ãƒ†ã‚£ã‚¯ãƒ«ã®æ›´æ–°
    updateParticles() {
      for (let i = 0; i < this.particles.length; i++) {
        const p = this.particles[i];

        // æœ€ã‚‚è¿‘ã„æµä½“å ´ã‚’å–å¾—
        const field = this.getClosestFlowField(p.x, p.y);

        // æµä½“å ´ã«åŸºã¥ã„ã¦é€Ÿåº¦ã‚’æ›´æ–°
        p.speedX += Math.cos(field.angle) * field.strength;
        p.speedY += Math.sin(field.angle) * field.strength;

        // é€Ÿåº¦ã®æ¸›è¡°
        p.speedX *= 0.95;
        p.speedY *= 0.95;

        // ä½ç½®ã®æ›´æ–°
        p.x += p.speedX;
        p.y += p.speedY;

        // ç”»é¢å¤–ã«å‡ºãŸå ´åˆã®å‡¦ç†
        if (p.x < 0) p.x = this.width;
        if (p.x > this.width) p.x = 0;
        if (p.y < 0) p.y = this.height;
        if (p.y > this.height) p.y = 0;
      }
    }

    // æµä½“å ´ã®æ›´æ–°
    updateFlowField() {
      const time = Date.now() * 0.001;

      for (let i = 0; i < this.flowField.length; i++) {
        const field = this.flowField[i];
        field.angle = (Math.sin(time * 0.1 + field.x * 0.01) + Math.cos(time * 0.15 + field.y * 0.01)) * Math.PI;
        field.strength = (Math.sin(time * 0.2 + field.x * 0.005 + field.y * 0.005) + 1) * 0.3 + 0.1;
      }
    }

    // æç”»
    draw() {
      this.ctx.clearRect(0, 0, this.width, this.height);

      // ãƒ‘ãƒ¼ãƒ†ã‚£ã‚¯ãƒ«ã®æç”»
      for (let i = 0; i < this.particles.length; i++) {
        const p = this.particles[i];

        this.ctx.beginPath();
        const gradient = this.ctx.createRadialGradient(p.x, p.y, 0, p.x, p.y, p.size * 3);
        gradient.addColorStop(0, p.color);
        gradient.addColorStop(1, 'transparent');
        this.ctx.fillStyle = gradient;
        this.ctx.arc(p.x, p.y, p.size * 3, 0, Math.PI * 2);
        this.ctx.fill();
      }

      // ãƒ‘ãƒ¼ãƒ†ã‚£ã‚¯ãƒ«é–“ã®æ¥ç¶šç·š
      this.ctx.strokeStyle = 'rgba(100, 150, 255, 0.05)';
      this.ctx.lineWidth = 0.5;

      for (let i = 0; i < this.particles.length; i++) {
        const p1 = this.particles[i];

        for (let j = i + 1; j < this.particles.length; j++) {
          const p2 = this.particles[j];
          const dx = p1.x - p2.x;
          const dy = p1.y - p2.y;
          const dist = Math.sqrt(dx * dx + dy * dy);

          if (dist < 100) {
            this.ctx.beginPath();
            this.ctx.moveTo(p1.x, p1.y);
            this.ctx.lineTo(p2.x, p2.y);
            this.ctx.globalAlpha = 1 - dist / 100;
            this.ctx.stroke();
            this.ctx.globalAlpha = 1;
          }
        }
      }
    }

    // ã‚¢ãƒ‹ãƒ¡ãƒ¼ã‚·ãƒ§ãƒ³ãƒ«ãƒ¼ãƒ—
    animate() {
      this.updateFlowField();
      this.updateParticles();
      this.draw();

      requestAnimFrame(() => this.animate());
    }
  }

  // é‡å­æ³¢å‹•åŠ¹æœ
  class QuantumWaveEffect {
    constructor(width, height) {
      this.canvas = createCanvas(width, height);
      this.ctx = this.canvas.getContext('2d');
      this.width = width;
      this.height = height;
      this.waves = [];
      this.init();
    }

    init() {
      // æ³¢ã®åˆæœŸåŒ–
      for (let i = 0; i < 5; i++) {
        this.waves.push({
          amplitude: Math.random() * 20 + 10,
          wavelength: Math.random() * 200 + 100,
          speed: Math.random() * 0.02 + 0.01,
          phase: Math.random() * Math.PI * 2
        });
      }

      this.animate();
    }

    // æ³¢ã®é«˜ã•ã‚’è¨ˆç®—
    getWaveHeight(x, y, time) {
      let height = 0;

      for (let i = 0; i < this.waves.length; i++) {
        const wave = this.waves[i];
        const angle = (x / wave.wavelength + time * wave.speed + wave.phase);
        height += Math.sin(angle) * wave.amplitude;
      }

      return height;
    }

    // æç”»
    draw() {
      const time = Date.now() * 0.001;
      this.ctx.clearRect(0, 0, this.width, this.height);

      // ã‚°ãƒ©ãƒ‡ãƒ¼ã‚·ãƒ§ãƒ³ã®è¨­å®š
      const gradient = this.ctx.createLinearGradient(0, 0, this.width, this.height);
      gradient.addColorStop(0, 'rgba(30, 64, 175, 0.1)');
      gradient.addColorStop(0.5, 'rgba(37, 99, 235, 0.1)');
      gradient.addColorStop(1, 'rgba(30, 58, 138, 0.1)');

      this.ctx.fillStyle = gradient;

      // æ³¢ã®æç”»
      this.ctx.beginPath();
      this.ctx.moveTo(0, this.height / 2);

      for (let x = 0; x < this.width; x += 5) {
        const y = this.height / 2 + this.getWaveHeight(x, 0, time);
        this.ctx.lineTo(x, y);
      }

      this.ctx.lineTo(this.width, this.height);
      this.ctx.lineTo(0, this.height);
      this.ctx.closePath();
      this.ctx.fill();

      // å¹²æ¸‰ãƒ‘ã‚¿ãƒ¼ãƒ³ã®æç”»
      this.ctx.strokeStyle = 'rgba(255, 255, 255, 0.05)';
      this.ctx.lineWidth = 1;

      for (let y = 0; y < this.height; y += 40) {
        this.ctx.beginPath();

        for (let x = 0; x < this.width; x += 5) {
          const waveHeight = this.getWaveHeight(x, y, time) * 0.3;
          const yPos = y + waveHeight;

          if (x === 0) {
            this.ctx.moveTo(x, yPos);
          } else {
            this.ctx.lineTo(x, yPos);
          }
        }

        this.ctx.stroke();
      }
    }

    // ã‚¢ãƒ‹ãƒ¡ãƒ¼ã‚·ãƒ§ãƒ³ãƒ«ãƒ¼ãƒ—
    animate() {
      this.draw();
      requestAnimFrame(() => this.animate());
    }
  }

  // 3Dç©ºé–“æ­ªã¿åŠ¹æœ
  class SpaceDistortionEffect {
    constructor(width, height) {
      this.canvas = createCanvas(width, height);
      this.ctx = this.canvas.getContext('2d');
      this.width = width;
      this.height = height;
      this.points = [];
      this.init();
    }

    init() {
      // 3Dç‚¹ã®åˆæœŸåŒ–
      for (let i = 0; i < 100; i++) {
        this.points.push({
          x: Math.random() * this.width,
          y: Math.random() * this.height,
          z: Math.random() * 500 - 250,
          size: Math.random() * 3 + 1,
          color: `hsla(${Math.random() * 60 + 210}, 100%, 70%, 0.2)`
        });
      }

      this.animate();
    }

    // 3Dç‚¹ã®æ›´æ–°
    updatePoints() {
      const time = Date.now() * 0.001;

      for (let i = 0; i < this.points.length; i++) {
        const point = this.points[i];

        // Zåº§æ¨™ã®æ›´æ–°ï¼ˆå¥¥è¡Œãæ–¹å‘ã®ç§»å‹•ï¼‰
        point.z += Math.sin(time * 0.5 + i * 0.1) * 2;

        // ç”»é¢å¤–ã«å‡ºãŸå ´åˆã®å‡¦ç†
        if (point.z > 250) point.z = -250;
        if (point.z < -250) point.z = 250;
      }
    }

    // æç”»
    draw() {
      this.ctx.clearRect(0, 0, this.width, this.height);

      // ç‚¹ã®æç”»ï¼ˆZåº§æ¨™ã«åŸºã¥ã„ã¦ã‚µã‚¤ã‚ºã¨é€æ˜åº¦ã‚’å¤‰æ›´ï¼‰
      for (let i = 0; i < this.points.length; i++) {
        const point = this.points[i];
        const scale = 500 / (500 + point.z); // é è¿‘æ„Ÿã®è¨ˆç®—
        const x = (point.x - this.width / 2) * scale + this.width / 2;
        const y = (point.y - this.height / 2) * scale + this.height / 2;
        const size = point.size * scale;
        const alpha = (point.z + 250) / 500; // Zåº§æ¨™ã«åŸºã¥ãé€æ˜åº¦

        this.ctx.beginPath();
        const gradient = this.ctx.createRadialGradient(x, y, 0, x, y, size * 3);
        gradient.addColorStop(0, point.color.replace('0.2', alpha.toFixed(2)));
        gradient.addColorStop(1, 'transparent');
        this.ctx.fillStyle = gradient;
        this.ctx.arc(x, y, size * 3, 0, Math.PI * 2);
        this.ctx.fill();
      }

      // ç‚¹é–“ã®æ¥ç¶šç·šï¼ˆZåº§æ¨™ãŒè¿‘ã„ç‚¹åŒå£«ã‚’æ¥ç¶šï¼‰
      this.ctx.strokeStyle = 'rgba(100, 150, 255, 0.05)';
      this.ctx.lineWidth = 0.5;

      for (let i = 0; i < this.points.length; i++) {
        const p1 = this.points[i];
        const scale1 = 500 / (500 + p1.z);
        const x1 = (p1.x - this.width / 2) * scale1 + this.width / 2;
        const y1 = (p1.y - this.height / 2) * scale1 + this.height / 2;

        for (let j = i + 1; j < this.points.length; j++) {
          const p2 = this.points[j];
          const scale2 = 500 / (500 + p2.z);
          const x2 = (p2.x - this.width / 2) * scale2 + this.width / 2;
          const y2 = (p2.y - this.height / 2) * scale2 + this.height / 2;

          const dx = x1 - x2;
          const dy = y1 - y2;
          const dz = p1.z - p2.z;
          const dist = Math.sqrt(dx * dx + dy * dy);
          const zDist = Math.abs(dz);

          if (dist < 100 && zDist < 100) {
            this.ctx.beginPath();
            this.ctx.moveTo(x1, y1);
            this.ctx.lineTo(x2, y2);
            this.ctx.globalAlpha = (1 - dist / 100) * (1 - zDist / 100) * 0.5;
            this.ctx.stroke();
            this.ctx.globalAlpha = 1;
          }
        }
      }
    }

    // ã‚¢ãƒ‹ãƒ¡ãƒ¼ã‚·ãƒ§ãƒ³ãƒ«ãƒ¼ãƒ—
    animate() {
      this.updatePoints();
      this.draw();
      requestAnimFrame(() => this.animate());
    }
  }

  // åŠ¹æœã®åˆæœŸåŒ–
  function initEffects() {
    // ã‚¦ã‚£ãƒ³ãƒ‰ã‚¦ã‚µã‚¤ã‚ºã®å–å¾—
    const width = window.innerWidth || document.documentElement.clientWidth || document.body.clientWidth;
    const height = window.innerHeight || document.documentElement.clientHeight || document.body.clientHeight;

    // å„åŠ¹æœã®åˆæœŸåŒ–
    setTimeout(() => {
      new FluidSimulation(width, height);
      new QuantumWaveEffect(width, height);
      new SpaceDistortionEffect(width, height);
    }, 100);
  }

  // DOMContentLoadedã‚¤ãƒ™ãƒ³ãƒˆã§åˆæœŸåŒ–
  if (document.readyState === 'loading') {
    document.addEventListener('DOMContentLoaded', initEffects);
  } else {
    initEffects();
  }
})();
