(function() {
  // requestAnimationFrameã®ãƒãƒªãƒ•ã‚£ãƒ«
  const requestAnimFrame = window.requestAnimationFrame || 
                          window.webkitRequestAnimationFrame || 
                          window.mozRequestAnimationFrame || 
                          function(callback) { window.setTimeout(callback, 1000/60); };

  // ãƒã‚¦ã‚¹ãƒã‚¸ã‚·ãƒ§ãƒ³ãƒˆãƒ©ãƒƒã‚­ãƒ³ã‚°
  let mouseX = 0;
  let mouseY = 0;
  let windowWidth = window.innerWidth || document.documentElement.clientWidth;
  let windowHeight = window.innerHeight || document.documentElement.clientHeight;

  // ãƒã‚¦ã‚¹ç§»å‹•ã‚¤ãƒ™ãƒ³ãƒˆãƒªã‚¹ãƒŠãƒ¼
  document.addEventListener('mousemove', function(e) {
    mouseX = e.clientX;
    mouseY = e.clientY;
    updateParallaxEffect();
  });

  // ãƒªã‚µã‚¤ã‚ºã‚¤ãƒ™ãƒ³ãƒˆãƒªã‚¹ãƒŠãƒ¼
  window.addEventListener('resize', function() {
    windowWidth = window.innerWidth || document.documentElement.clientWidth;
    windowHeight = window.innerHeight || document.documentElement.clientHeight;
  });

  // ãƒ‘ãƒ©ãƒ©ãƒƒã‚¯ã‚¹åŠ¹æœã®æ›´æ–°
  function updateParallaxEffect() {
    const parallaxLayers = document.querySelectorAll('.parallax-layer');

    // ãƒã‚¦ã‚¹ä½ç½®ã‚’æ­£è¦åŒ–ï¼ˆ-1 ã‹ã‚‰ 1 ã®ç¯„å›²ï¼‰
    const normalizedX = (mouseX / windowWidth) * 2 - 1;
    const normalizedY = (mouseY / windowHeight) * 2 - 1;

    parallaxLayers.forEach((layer, index) => {
      // å„ãƒ¬ã‚¤ãƒ¤ãƒ¼ã«ç•°ãªã‚‹å¼·åº¦ã®ãƒ‘ãƒ©ãƒ©ãƒƒã‚¯ã‚¹åŠ¹æœã‚’é©ç”¨
      const depthFactor = 0.05 * (index + 1);
      const moveX = normalizedX * 50 * depthFactor;
      const moveY = normalizedY * 50 * depthFactor;

      // 3Då¤‰æ›ã‚’é©ç”¨
      layer.style.transform = `translate3d(${moveX}px, ${moveY}px, 0)`;
    });

    // 3Dã‚­ãƒ¥ãƒ¼ãƒ–ã¨é‡å­ãƒ‘ãƒ¼ãƒ†ã‚£ã‚¯ãƒ«ã®ãƒã‚¦ã‚¹è¿½å¾“å›è»¢
    const shapes = document.querySelectorAll('.shape3d');
    shapes.forEach((shape) => {
      shape.style.transform = `rotateY(${normalizedX * 20}deg) rotateX(${normalizedY * -20}deg)`;
    });

    // é‡å­ãƒ‘ãƒ¼ãƒ†ã‚£ã‚¯ãƒ«ã‚‚è‹¥å¹²ãƒã‚¦ã‚¹ã«åå¿œã•ã›ã‚‹
    const particles = document.querySelectorAll('.quantum-particle');
    particles.forEach((particle) => {
      const randomOffset = Math.random() * 5;
      particle.style.transform = `translate3d(${normalizedX * 10 + randomOffset}px, ${normalizedY * 10 + randomOffset}px, 0)`;
    });
  }

  // ã‚­ãƒ£ãƒ³ãƒã‚¹ä½œæˆé–¢æ•°
  function createCanvas(width, height) {
    const canvas = document.createElement('canvas');
    canvas.width = width;
    canvas.height = height;
    canvas.style.position = 'absolute';
    canvas.style.top = '0';
    canvas.style.left = '0';
    canvas.style.width = '100%';
    canvas.style.height = '100%';
    canvas.style.pointerEvents = 'none';
    canvas.style.zIndex = '-1';
    canvas.style.opacity = '0.3';
    canvas.style.mixBlendMode = 'screen';
    document.querySelector('body').appendChild(canvas);
    return canvas;
  }

  // æµä½“ã‚·ãƒŸãƒ¥ãƒ¬ãƒ¼ã‚·ãƒ§ãƒ³
  class FluidSimulation {
    constructor(width, height) {
      this.canvas = createCanvas(width, height);
      this.ctx = this.canvas.getContext('2d');
      this.width = width;
      this.height = height;
      this.particles = [];
      this.flowField = [];
      this.mouseInfluence = { x: 0, y: 0, active: false, strength: 100 };
      this.init();

      // ãƒã‚¦ã‚¹å½±éŸ¿ã®ã‚¤ãƒ™ãƒ³ãƒˆãƒªã‚¹ãƒŠãƒ¼
      document.addEventListener('mousemove', this.handleMouseMove.bind(this));
      document.addEventListener('mousedown', () => { this.mouseInfluence.active = true; });
      document.addEventListener('mouseup', () => { this.mouseInfluence.active = false; });
    }

    handleMouseMove(e) {
      // ã‚­ãƒ£ãƒ³ãƒã‚¹å†…ã®ç›¸å¯¾åº§æ¨™ã«å¤‰æ›
      const rect = this.canvas.getBoundingClientRect();
      this.mouseInfluence.x = (e.clientX - rect.left) * (this.width / rect.width);
      this.mouseInfluence.y = (e.clientY - rect.top) * (this.height / rect.height);
    }

    init() {
      // æµä½“å ´ã®åˆæœŸåŒ– - ã‚ˆã‚Šç´°ã‹ã
      for (let y = 0; y < this.height; y += 15) {
        for (let x = 0; x < this.width; x += 15) {
          this.flowField.push({
            x: x,
            y: y,
            angle: Math.random() * Math.PI * 2,
            strength: Math.random() * 0.5 + 0.1,
            z: Math.random() * 200 - 100 // Zåº§æ¨™ã‚’è¿½åŠ 
          });
        }
      }

      // ãƒ‘ãƒ¼ãƒ†ã‚£ã‚¯ãƒ«ã®åˆæœŸåŒ– - ã‚ˆã‚Šå¤šãã€3Då±æ€§ã‚’è¿½åŠ 
      for (let i = 0; i < 150; i++) {
        this.particles.push({
          x: Math.random() * this.width,
          y: Math.random() * this.height,
          z: Math.random() * 200 - 100, // Zåº§æ¨™ï¼ˆå¥¥è¡Œãï¼‰
          size: Math.random() * 3 + 1,
          speedX: 0,
          speedY: 0,
          speedZ: 0, // Zæ–¹å‘ã®é€Ÿåº¦
          color: `hsla(${Math.random() * 60 + 200}, 100%, 70%, 0.3)`
        });
      }

      this.animate();
    }

    // æœ€ã‚‚è¿‘ã„æµä½“å ´ã®ã‚¤ãƒ³ãƒ‡ãƒƒã‚¯ã‚¹ã‚’å–å¾—
    getClosestFlowField(x, y, z) {
      let closestIndex = 0;
      let closestDist = Number.MAX_VALUE;

      for (let i = 0; i < this.flowField.length; i++) {
        const field = this.flowField[i];
        const dx = field.x - x;
        const dy = field.y - y;
        const dz = field.z - z;
        const dist = dx * dx + dy * dy + dz * dz; // 3Dè·é›¢

        if (dist < closestDist) {
          closestDist = dist;
          closestIndex = i;
        }
      }

      return this.flowField[closestIndex];
    }

    // ãƒ‘ãƒ¼ãƒ†ã‚£ã‚¯ãƒ«ã®æ›´æ–°
    updateParticles() {
      for (let i = 0; i < this.particles.length; i++) {
        const p = this.particles[i];

        // æœ€ã‚‚è¿‘ã„æµä½“å ´ã‚’å–å¾—
        const field = this.getClosestFlowField(p.x, p.y, p.z);

        // æµä½“å ´ã«åŸºã¥ã„ã¦é€Ÿåº¦ã‚’æ›´æ–°
        p.speedX += Math.cos(field.angle) * field.strength;
        p.speedY += Math.sin(field.angle) * field.strength;
        p.speedZ += (Math.sin(field.angle + Math.PI/4) * field.strength) * 0.3; // Zæ–¹å‘ã®å‹•ãã‚’è¿½åŠ 

        // ãƒã‚¦ã‚¹ã®å½±éŸ¿ï¼ˆ3Dç©ºé–“ã§ã®å¸å¼•ã¾ãŸã¯åç™ºï¼‰
        if (this.mouseInfluence.active) {
          const dx = this.mouseInfluence.x - p.x;
          const dy = this.mouseInfluence.y - p.y;
          const dist = Math.sqrt(dx * dx + dy * dy);

          if (dist < this.mouseInfluence.strength) {
            const force = (this.mouseInfluence.strength - dist) / this.mouseInfluence.strength;
            p.speedX += dx * force * 0.02;
            p.speedY += dy * force * 0.02;
            p.speedZ += Math.sin(Date.now() * 0.001) * force * 0.01; // Zæ–¹å‘ã«ã‚‚å½±éŸ¿
          }
        }

        // é€Ÿåº¦ã®æ¸›è¡°
        p.speedX *= 0.95;
        p.speedY *= 0.95;
        p.speedZ *= 0.97; // Zæ–¹å‘ã¯ã‚ˆã‚Šç·©ã‚„ã‹ã«æ¸›è¡°

        // ä½ç½®ã®æ›´æ–°
        p.x += p.speedX;
        p.y += p.speedY;
        p.z += p.speedZ;

        // ç”»é¢å¤–ã«å‡ºãŸå ´åˆã®å‡¦ç†ï¼ˆ3Dç©ºé–“ï¼‰
        if (p.x < 0) p.x = this.width;
        if (p.x > this.width) p.x = 0;
        if (p.y < 0) p.y = this.height;
        if (p.y > this.height) p.y = 0;
        if (p.z < -100) p.z = 100;
        if (p.z > 100) p.z = -100;
      }
    }

    // æµä½“å ´ã®æ›´æ–°
    updateFlowField() {
      const time = Date.now() * 0.001;

      for (let i = 0; i < this.flowField.length; i++) {
        const field = this.flowField[i];
        // ã‚ˆã‚Šè¤‡é›‘ãªæ™‚é–“å¤‰å‹•ãƒ‘ã‚¿ãƒ¼ãƒ³
        field.angle = (Math.sin(time * 0.1 + field.x * 0.01 + field.z * 0.005) + 
                     Math.cos(time * 0.15 + field.y * 0.01 - field.z * 0.003)) * Math.PI;
        field.strength = (Math.sin(time * 0.2 + field.x * 0.005 + field.y * 0.005 + field.z * 0.002) + 1) * 0.3 + 0.1;

        // Zåº§æ¨™ã‚‚æ™‚é–“ã¨ã¨ã‚‚ã«å¤‰å‹•
        field.z += Math.sin(time * 0.05 + i * 0.01) * 0.5;
        if (field.z < -100) field.z = 100;
        if (field.z > 100) field.z = -100;
      }
    }

    // æç”»
    draw() {
      this.ctx.clearRect(0, 0, this.width, this.height);

      // ãƒ‘ãƒ¼ãƒ†ã‚£ã‚¯ãƒ«ã®æç”»ï¼ˆZåº§æ¨™ã«ã‚ˆã‚‹å¤§ãã•ã¨é€æ˜åº¦ã®å¤‰æ›´ï¼‰
      for (let i = 0; i < this.particles.length; i++) {
        const p = this.particles[i];

        // Zåº§æ¨™ã«åŸºã¥ãå¥¥è¡Œãè¡¨ç¾
        const scale = 1000 / (1000 + p.z * 2); // å¥¥è¡Œãã«åŸºã¥ãã‚¹ã‚±ãƒ¼ãƒªãƒ³ã‚°
        const size = p.size * scale * 3;
        const x = p.x;
        const y = p.y;
        const alpha = (p.z + 100) / 200 * 0.6 + 0.2; // Zåº§æ¨™ã«åŸºã¥ãé€æ˜åº¦

        // ç«‹ä½“æ„Ÿã®ã‚ã‚‹æç”»
        this.ctx.beginPath();
        const gradient = this.ctx.createRadialGradient(x, y, 0, x, y, size);
        gradient.addColorStop(0, p.color.replace('0.3', alpha.toFixed(2)));
        gradient.addColorStop(1, 'transparent');
        this.ctx.fillStyle = gradient;
        this.ctx.arc(x, y, size, 0, Math.PI * 2);
        this.ctx.fill();

        // å…‰ã®åŠ¹æœï¼ˆãƒã‚¤ãƒ©ã‚¤ãƒˆï¼‰
        this.ctx.beginPath();
        const highlightSize = size * 0.3;
        const highlightGradient = this.ctx.createRadialGradient(
          x - size * 0.2, y - size * 0.2, 0,
          x - size * 0.2, y - size * 0.2, highlightSize
        );
        highlightGradient.addColorStop(0, `rgba(255, 255, 255, ${alpha * 0.8})`);
        highlightGradient.addColorStop(1, 'transparent');
        this.ctx.fillStyle = highlightGradient;
        this.ctx.arc(x - size * 0.2, y - size * 0.2, highlightSize, 0, Math.PI * 2);
        this.ctx.fill();
      }

      // 3Dç©ºé–“å†…ã®ãƒ‘ãƒ¼ãƒ†ã‚£ã‚¯ãƒ«é–“ã®æ¥ç¶šç·š
      this.ctx.strokeStyle = 'rgba(100, 150, 255, 0.05)';
      this.ctx.lineWidth = 0.5;

      for (let i = 0; i < this.particles.length; i++) {
        const p1 = this.particles[i];

        for (let j = i + 1; j < this.particles.length; j++) {
          const p2 = this.particles[j];
          const dx = p1.x - p2.x;
          const dy = p1.y - p2.y;
          const dz = p1.z - p2.z; // Zåº§æ¨™ã®å·®ã‚‚è€ƒæ…®
          const dist = Math.sqrt(dx * dx + dy * dy);
          const dist3d = Math.sqrt(dx * dx + dy * dy + dz * dz); // 3Dè·é›¢

          // 3Dç©ºé–“ã§è¿‘ã„å ´åˆã®ã¿ç·šã‚’æç”»
          if (dist3d < 120) {
            const alpha = (1 - dist3d / 120) * 0.1;

            this.ctx.beginPath();
            this.ctx.moveTo(p1.x, p1.y);
            this.ctx.lineTo(p2.x, p2.y);
            this.ctx.globalAlpha = alpha;
            this.ctx.stroke();
          }
        }
      }

      this.ctx.globalAlpha = 1;
    }

    // ã‚¢ãƒ‹ãƒ¡ãƒ¼ã‚·ãƒ§ãƒ³ãƒ«ãƒ¼ãƒ—
    animate() {
      this.updateFlowField();
      this.updateParticles();
      this.draw();

      requestAnimFrame(() => this.animate());
    }
  }

  // é‡å­æ³¢å‹•åŠ¹æœã®å¼·åŒ–ã‚¯ãƒ©ã‚¹
  class EnhancedQuantumWaveEffect extends QuantumWaveEffect {
    constructor(width, height) {
      super(width, height);
      this.zOffset = 0; // Zè»¸ã®ã‚ªãƒ•ã‚»ãƒƒãƒˆï¼ˆå¥¥è¡Œãï¼‰
      this.mouseInfluence = { x: 0, y: 0, active: false };

      // ãƒã‚¦ã‚¹å½±éŸ¿ã®ã‚¤ãƒ™ãƒ³ãƒˆãƒªã‚¹ãƒŠãƒ¼
      document.addEventListener('mousemove', this.handleMouseMove.bind(this));
    }

    handleMouseMove(e) {
      const rect = this.canvas.getBoundingClientRect();
      this.mouseInfluence.x = (e.clientX - rect.left) * (this.width / rect.width);
      this.mouseInfluence.y = (e.clientY - rect.top) * (this.height / rect.height);
    }

    // æ³¢ã®é«˜ã•ã‚’è¨ˆç®—ï¼ˆ3DåŠ¹æœã‚’å¼·åŒ–ï¼‰
    getWaveHeight(x, y, time) {
      let height = 0;

      for (let i = 0; i < this.waves.length; i++) {
        const wave = this.waves[i];
        // ã‚ˆã‚Šè¤‡é›‘ãª3Dã®ã‚ˆã†ãªæ³¢å‹•ãƒ‘ã‚¿ãƒ¼ãƒ³
        const angle = (x / wave.wavelength + y / (wave.wavelength * 2) + time * wave.speed + wave.phase);
        height += Math.sin(angle) * wave.amplitude;

        // Zè»¸ã®å¤‰å‹•ã‚‚è¿½åŠ 
        this.zOffset = Math.sin(time * 0.2) * 50;
      }

      // ãƒã‚¦ã‚¹ä½ç½®ã«ã‚ˆã‚‹æ³¢ã®æ­ªã¿
      const dx = x - this.mouseInfluence.x;
      const dy = y - this.mouseInfluence.y;
      const dist = Math.sqrt(dx * dx + dy * dy);

      if (dist < 150) {
        height += (150 - dist) / 10 * Math.sin(time * 5);
      }

      return height;
    }

    // æç”»ãƒ¡ã‚½ãƒƒãƒ‰ã®ã‚ªãƒ¼ãƒãƒ¼ãƒ©ã‚¤ãƒ‰
    draw() {
      const time = Date.now() * 0.001;
      this.ctx.clearRect(0, 0, this.width, this.height);

      // ã‚ˆã‚Šæ´—ç·´ã•ã‚ŒãŸã‚°ãƒ©ãƒ‡ãƒ¼ã‚·ãƒ§ãƒ³
      const gradient = this.ctx.createLinearGradient(0, 0, this.width, this.height);
      gradient.addColorStop(0, 'rgba(30, 64, 175, 0.08)');
      gradient.addColorStop(0.3, 'rgba(37, 99, 235, 0.1)');
      gradient.addColorStop(0.7, 'rgba(59, 130, 246, 0.08)');
      gradient.addColorStop(1, 'rgba(30, 58, 138, 0.06)');

      this.ctx.fillStyle = gradient;

      // 3DåŠ¹æœã®ã‚ã‚‹æ³¢ã®æç”»
      this.ctx.beginPath();
      this.ctx.moveTo(0, this.height / 2);

      // æ³¢ã®å¯†åº¦ã‚’ä¸Šã’ã¦æ»‘ã‚‰ã‹ã«
      for (let x = 0; x < this.width; x += 3) {
        const y = this.height / 2 + this.getWaveHeight(x, 0, time);
        this.ctx.lineTo(x, y);
      }

      this.ctx.lineTo(this.width, this.height);
      this.ctx.lineTo(0, this.height);
      this.ctx.closePath();
      this.ctx.fill();

      // è¤‡æ•°ã®å±¤ã«ã‚ˆã‚‹3Dæ„Ÿã®å¼·åŒ–
      for (let layer = 1; layer <= 3; layer++) {
        const layerOffset = layer * 40;
        const alpha = 0.05 - layer * 0.01;

        this.ctx.strokeStyle = `rgba(255, 255, 255, ${alpha})`;
        this.ctx.lineWidth = 1;

        // å„ãƒ¬ã‚¤ãƒ¤ãƒ¼ã®æ³¢ç·š
        for (let y = 0; y < this.height; y += 40) {
          this.ctx.beginPath();

          for (let x = 0; x < this.width; x += 3) {
            const waveHeight = this.getWaveHeight(x, y + layerOffset, time + layer * 0.2) * 0.3;
            const yPos = y + waveHeight + this.zOffset / layer;

            if (x === 0) {
              this.ctx.moveTo(x, yPos);
            } else {
              this.ctx.lineTo(x, yPos);
            }
          }

          this.ctx.stroke();
        }
      }
    }
  }

  // æ—¢å­˜ã®3Dç©ºé–“æ­ªã¿åŠ¹æœã‚¯ãƒ©ã‚¹
  // ... existing code ...

  // åŠ¹æœã®åˆæœŸåŒ–
  function initEffects() {
    // ã‚¦ã‚£ãƒ³ãƒ‰ã‚¦ã‚µã‚¤ã‚ºã®å–å¾—
    const width = window.innerWidth || document.documentElement.clientWidth || document.body.clientWidth;
    const height = window.innerHeight || document.documentElement.clientHeight || document.body.clientHeight;

    // å„åŠ¹æœã®åˆæœŸåŒ–
    setTimeout(() => {
      new FluidSimulation(width, height);
      new EnhancedQuantumWaveEffect(width, height);
      new SpaceDistortionEffect(width, height);

      // ãƒã‚¦ã‚¹ãƒ‘ãƒ©ãƒ©ãƒƒã‚¯ã‚¹åŠ¹æœã®åˆæœŸã‚»ãƒƒãƒˆã‚¢ãƒƒãƒ—
      setInterval(updateParallaxEffect, 1000/30); // 30fpsæ›´æ–°
    }, 100);
  }

  // DOMContentLoadedã‚¤ãƒ™ãƒ³ãƒˆã§åˆæœŸåŒ–
  if (document.readyState === 'loading') {
    document.addEventListener('DOMContentLoaded', initEffects);
  } else {
    initEffects();
  }
})();
